SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_ad_hoc_request_c.closed_on_key' ELSE 'SUCCESS' END as Message FROM whirlpool_mdsdb.u_ad_hoc_request_final  SRC JOIN whirlpool_mdwdb.f_ad_hoc_request_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN whirlpool_mdwdb.d_calendar_date LKP ON  (COALESCE(date_format(convert_tz(SRC.closed_at,'GMT','America/New_York'),'%Y%m%d'),'UNSPECIFIED') = LKP.row_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.closed_at IS NULL THEN 0 else -1 end)<> (TRGT.closed_on_key) 
