SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_change_request.sub_category_src_c_key' ELSE 'SUCCESS' END as Message FROM rogers_mdsdb.change_request_final  SRC JOIN rogers_mdwdb.d_change_request TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN rogers_mdwdb.d_lov LKP ON LKP.row_id=(COALESCE(CONCAT('SUB_CATEGORY','~','CHANGE_REQUEST','~','~','~',UPPER(SRC.u_subcategory)),'UNSPECIFIED') ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_subcategory IS NULL THEN 0 else -1 end)<> (TRGT.subcategory_src_c_key) 
and SRC.CDCTYPE='X' and LKP.soft_deleted_flag='N';

