SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.last_resolved_by_key' ELSE 'SUCCESS' END as Message 
FROM rogers_mdsdb.incident_final  SRC JOIN rogers_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  
LEFT JOIN rogers_mdwdb.d_internal_contact LKP ON ( concat( 'INTERNAL_CONTACT~' ,coalesce( SRC.u_resolved_by,SRC.closed_by)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id )WHERE COALESCE(LKP.row_key,CASE WHEN coalesce( SRC.u_resolved_by,SRC.closed_by) IS NULL THEN 0 else -1 end)<> (TRGT.resolved_by_key) 
