SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1) 
 THEN 'MDS to DWH data validation failed for d_incident.groups_for_rca_c' ELSE 'SUCCESS' END as Message
 FROM rogers_mdsdb.incident_final  SRC left JOIN rogers_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )
 WHERE
case  when  incident.description like '%Spam%' or incident.description like '%Phish%' or incident.description like '%Phishing@rci.rogers.com%' or incident.description like '%scam%' or incident.description like '%spyware%' or  incident.short_description like '%Spam%' or incident.short_description like '%Phish%' or incident.short_description like '%Phishing@rci.rogers.com%'  or  incident.short_description like '%scam%' or incident.short_description like '%spyware%'   or incident.close_notes like '%Spam%' or incident.close_notes like '%Phish%' or incident.close_notes like '%Phishing@rci.rogers.com%'  or  incident.close_notes like '%scam%' or incident.close_notes like '%spyware%'  or  incident.close_code ='Phishing' or incident.u_knowledge_record = 'ff61a82d4f0bf500cdae00fe9310c7ec' then 'Y' else 'N'  end <> phishing_c