SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_problem.u_known_error_notes_c' ELSE 'SUCCESS' END as Message FROM rogers_mdsdb.problem_final  SRC JOIN rogers_mdwdb.d_problem TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.u_known_error_notes <> (TRGT.u_known_error_notes_c) 
