SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_incident.client_duration_c' ELSE 'SUCCESS' END as Message FROM rogersdev_mdsdb.incident_final  SRC JOIN rogersdev_mdwdb.f_incident TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.u_client_duration <> (TRGT.client_duration_c) 
