SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_problem.problem_change_request_c_key' ELSE 'SUCCESS' END as Message FROM rogersdev_mdsdb.problem_final  SRC JOIN rogersdev_mdwdb.f_problem TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN rogersdev_mdwdb.d_change_request LKP ON ( concat( '' ,upper( SRC.u_change)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_change IS NULL THEN 0 else -1 end)<> (TRGT.problem_change_request_c_key) 
