SELECT CASE WHEN count(1) >0  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1) >0   THEN 'MDS to DWH data validation failed for d_incident.due_on' ELSE 'SUCCESS' END as Message from schneider_mdsdb.hpd_help_desk_final  SRC JOIN schneider_mdwdb.d_incident TRGT ON (SRC.entry_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id AND SRC.sourceinstance = TRGT.source_id ) WHERE FROM_UNIXTIME(SRC.required_resolution_datetime)<> (TRGT.due_on) 
