SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.service_type_key' ELSE 'SUCCESS' END as Message from schneider_mdsdb.hpd_help_desk_final  SRC JOIN schneider_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.sourceid AND SRC.sourceinstance = TRGT.sourceid ) 
LEFT JOIN schneider_mdwdb.d_lov lkp ON (concat( 'SERVICE_TYPE~INCIDENT~~~' ,upper( SRC.service_type)) = lkp.row_id AND SRC.sourceinstance = lkp.source_id ) WHERE  LEFT JOIN schneider_mdwdb.d_lov lkp ON (concat( 'SERVICE_TYPE~INCIDENT~~~' ,upper( SRC.service_type)) = lkp.row_id AND SRC.sourceinstance = lkp.source_id ) WHERE COALESCE(lkp.row_key,CASE WHEN SRC.service_type IS NULL THEN 0 else -1 end) <> coalesce(TRGT.service_type_key,'UNSPECIFIED');
