SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.Service_restoration_flag' ELSE 'SUCCESS' END as Message FROM schneider_mdsdb.hpd_help_desk_final  SRC JOIN schneider_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.sourceid  AND SRC.sourceinstance = TRGT.sourceid )  WHERE (CASE WHEN SRC.service_type = 'User Service Restoration' then 'Y' else 'N' END) <> (TRGT.Service_restoration_flag) 
