SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.close_code_src_key' ELSE 'SUCCESS' END as Message FROM schneider_mdsdb.hpd_help_desk_final  SRC JOIN schneider_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.sourceid AND SRC.sourceinstance = TRGT.sourceid )  and SRC.status='Closed'
LEFT JOIN schneider_mdwdb.d_lov LKP ON ( concat( 'STATUS_REASON~INCIDENT~~~' ,upper( SRC.status_reason)) = LKP.row_id AND SRC.sourceinstance = LKP.sourceid ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.status_reason IS NULL THEN 0 else -1 end)<> (TRGT.close_code_src_key) 
