SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_work_order_task.age' ELSE 'SUCCESS' END as Message FROM <<tenant>>_mdsdb.tms_task_final  SRC JOIN <<tenant>>_mdwdb.d_work_order_task TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )
JOIN schneider_mdwdb.d_lov_map LKP ON TRGT.state_src_key = LKP.src_key
AND LKP.dimension_wh_code IN ('OPEN') and LKP.dimension_class = 'STATE~WORK_ORDER_TASK'
WHERE TIMESTAMPDIFF(SECOND, convert_tz(convert_tz(SRC.reported_date,'GMT','America/Los_Angeles'),'America/Los_Angeles', 'GMT' ), (SELECT CONVERT_TZ(max(lastupdated),'America/Los_Angeles', 'GMT') AS lastupdated FROM schneider_mdwdb.d_o_data_freshness WHERE sourcename like '%' and etl_run_number=TRGT.etl_run_number)) <> TRGT.age)a; 