SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_work_order_task.opened_on_key' ELSE 'SUCCESS' END as Message  
FROM schneider_mdsdb.tms_task_final  SRC JOIN schneider_mdwdb.f_work_order_task TRGT ON (SRC.task_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  
LEFT JOIN schneider_mdwdb.d_calendar_date LKP ON  DATE_FORMAT(TRGT.created_on,'%Y%m%d') = LKP.row_id  WHERE COALESCE(LKP.row_key,CASE WHEN TRGT.created_on IS NULL THEN 0 else -1 end) <> (TRGT.opened_on_key) ;