SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_work_order_task.opened_on_key' ELSE 'SUCCESS' END as Message FROM schneider_mdsdb.tms_task_final  SRC JOIN schneider_mdwdb.f_work_order_task TRGT ON (SRC.task_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN schneider_mdwdb.d_calendar_date LKP ON  convert_tz(SRC.create_date,'GMT','America/Los_Angeles') = LKP.row_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.create_date IS NULL THEN 0 else -1 end)<> (TRGT.opened_on_key) 
