SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, 
CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_change_request.change_state_c_key' ELSE 'SUCCESS' END as Message FROM discover_mdsdb.change_request_final  SRC JOIN discover_mdwdb.d_change_request TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN discover_mdwdb.d_lov LKP ON ( concat( 'STATE~CHANGE_REQUEST~' ,upper( SRC.state)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.state IS NULL THEN 0 else -1 end)<> (TRGT.change_state_c_key)  and SRC.cdctype='X'
