SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.major_inc_configuration_item_c_key' ELSE 'SUCCESS' END as Message FROM discover_mdsdb.incident_final  SRC JOIN discover_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN discover_mdwdb.d_configuration_item LKP ON ( concat( '' ,upper( SRC.cmdb_ci)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.cmdb_ci IS NULL THEN 0 else -1 end)<> (TRGT.major_inc_configuration_item_c_key) 
