SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, 
CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_problem_task.problem_task_priority_c_key' ELSE 'SUCCESS' END as Message FROM discover_mdsdb.problem_task_final  SRC JOIN discover_mdwdb.d_problem_task TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN discover_mdwdb.d_lov LKP ON ( concat( 'PRIORITY~PROBLEM_TASK~' ,upper( SRC.priority)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.priority IS NULL THEN 0 else -1 end)<> (TRGT.problem_task_priority_c_key) and SRC.cdctype='X'
