SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_project_info_c.project_info_status_c_key' ELSE 'SUCCESS' END as Message 
FROM discover_mdsdb.u_project_info_final  SRC 
JOIN discover_mdwdb.d_project_info_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  
LEFT JOIN discover_mdwdb.d_lov LKP ON (CONCAT('U_STATUS_C~PROJECT_INFO_C~',SRC.u_status) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_status IS NULL THEN 0 else -1 end) <> (TRGT.project_info_status_c_key)  and SRC.cdctype='X'