SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_project_info_c.u_project_number_c' ELSE 'SUCCESS' END as Message FROM discover_mdsdb.u_project_info_final  SRC JOIN discover_mdwdb.d_project_info_c TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE (COALESCE(SRC.u_project_number,'UNSPECIFIED')) <> (TRGT.u_project_number_c) and SRC.cdctype='X'
