SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_fmr_product_line_c.install_status_src_c_key' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.u_fmr_product_line_final  SRC JOIN fidelity_mdwdb.d_fmr_product_line_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN fidelity_mdwdb.d_lov LKP ON ( concat( 'INSTALL_STATUS~FMR_PRODUCT_LINE_C~~~' ,upper( SRC.install_status)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.install_status IS NULL THEN 0 else -1 end)<> (TRGT.install_status_src_c_key) 
