SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_change_request.batch_onetime_platform_src_code_c' ELSE 'SUCCESS' END as Message FROM <<tenant>>_mdsdb.change_request_final  SRC JOIN <<tenant>>_mdwdb.d_change_request TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.u_batch_onetime_platform <> (TRGT.batch_onetime_platform_src_code_c) 
