SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_fmr_product_c.cost_center_update_date_c' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.u_fmr_product_final  SRC JOIN fidelity_mdwdb.d_fmr_product_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id ) WHERE convert_tz(SRC.u_cost_center_update,'GMT','America/New_York')<> (TRGT.cost_center_update_date_c) 
