SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_fmr_product_line_c.' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.u_fmr_product_line_final  SRC JOIN fidelity_mdwdb.d_fmr_product_line_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN fidelity_mdwdb.'' LKP ON ( concat( cost_center ,upper( SRC.cost_center_c_key)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.cost_center_c_key IS NULL THEN 0 else -1 end)<> (TRGT.) 
