SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_product_model.long_desc' ELSE 'SUCCESS' END as Message FROM <<tenant>>_mdsdb.cmdb_model_final  SRC JOIN <<tenant>>_mdwdb.d_product_model TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.comments <> (TRGT.long_desc) 
