SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_outage.external_impacted_count_c' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.cmdb_ci_outage_final  SRC JOIN fidelity_mdwdb.f_outage TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE coalesce(SRC.u_external_impacted_count,0) <> (TRGT.external_impacted_count_c) and SRC.cdctype<>'D'
