SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_product_model.short_desc' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.cmdb_model_final  SRC JOIN fidelity_mdwdb.d_product_model TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.short_description <> (TRGT.short_desc) 
