SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_fmr_product_c.fmr_product_line_c_key' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.u_fmr_product_final  SRC JOIN fidelity_mdwdb.d_fmr_product_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN fidelity_mdwdb.d_fmr_product_line_c LKP ON ( concat( '' ,upper( SRC.u_product_line_identifier)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_product_line_identifier IS NULL THEN 0 else -1 end)<> (TRGT.fmr_product_line_c_key) 
