SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_fmr_product_c.product_install_status_src_code_c' ELSE 'SUCCESS' END as Message FROM fidelity_mdsdb.u_fmr_product_final  SRC JOIN fidelity_mdwdb.d_fmr_product_c TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.install_status <> (TRGT.product_install_status_src_code_c) 
