SELECT CASE WHEN count(1) > 0 THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1) >0 THEN 'MDS to DWH data validation failed for d_incident_asc_c.due_on' ELSE 'SUCCESS' END as Message  FROM mercury_mdsdb.u_asc_ticket_final SRC  LEFT JOIN mercury_mdwdb.d_incident_asc_c TRGT  ON (SRC.sys_id =TRGT.row_id   AND SRC.sourceinstance= TRGT.source_id  ) WHERE 
coalesce(convert_tz(SRC.due_date,'GMT','America/Los_Angeles'),-99)<> coalesce(TRGT.due_on,-99)
