SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_problem.category_src_key' ELSE 'SUCCESS' END as Message FROM rogers_mdsdb.problem_final  SRC JOIN rogers_mdwdb.f_problem TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN rogers_mdwdb.d_lov LKP ON ( concat( 'CATEGORY~PROBLEM~' ,upper( SRC.u_category)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_category IS NULL THEN 0 else -1 end)<> (TRGT.category_src_key)
and SRC.CDCTYPE='X' and LKP.soft_deleted_flag='N' 
