SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_problem.related_problem_c_key' ELSE 'SUCCESS' END as Message FROM rogers_mdsdb.problem_final  SRC JOIN rogers_mdwdb.f_problem TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN rogers_mdwdb.d_problem LKP ON ( concat( '' ,upper( SRC.u_problem)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_problem IS NULL THEN 0 else -1 end)<> (TRGT.related_problem_c_key) 
and SRC.CDCTYPE='X' and LKP.soft_deleted_flag='N'
