SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.u_nc_datafix_flag_c' ELSE 'SUCCESS' END as Message FROM cox_mdsdb.incident_final  SRC JOIN cox_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE (CASE WHEN SRC.u_nc_datafix = 1 then 'Y' else 'N' END) <> (TRGT.u_nc_datafix_flag_c) 
