SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_incident.incident_status_new_src_c_key' ELSE 'SUCCESS' END as Message FROM cox_mdsdb.incident_final  SRC JOIN cox_mdwdb.d_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN cox_mdwdb.d_lov LKP ON ( concat( 'STATUS_NEW_C~INCIDENT~' ,upper( SRC.u_status_new)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_status_new IS NULL THEN 0 else -1 end)<> (TRGT.incident_status_new_src_c_key) 
