SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_problem.problem_subcategory_c' ELSE 'SUCCESS' END as Message FROM cox_mdsdb.problem_final  SRC JOIN cox_mdwdb.d_problem TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE COALESCE(SRC.u_sub_category,'UNSPECIFIED') <> (TRGT.problem_subcategory_c) 
