SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_problem.problem_type_of_issue_c_key' ELSE 'SUCCESS' END as Message FROM cox_mdsdb.problem_final  SRC JOIN cox_mdwdb.d_problem TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN cox_mdwdb.d_lov LKP ON ( concat( 'TYPE_OF_ISSUE_C~PROBLEM~',upper(SRC.u_type_of_issue)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_type_of_issue IS NULL THEN 0 else -1 end)<> (TRGT.problem_type_of_issue_c_key) 
