SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_incident.child_incident_c_key' ELSE 'SUCCESS' END as Message FROM cox_mdsdb.incident_final  SRC JOIN cox_mdwdb.f_incident TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN cox_mdwdb.d_incident LKP ON ( concat( '' ,upper( SRC.u_child_incident)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_child_incident IS NULL THEN 0 else -1 end)<> (TRGT.child_incident_c_key) 
