SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for f_problem.requester_c_key' ELSE 'SUCCESS' END as Message FROM cox_mdsdb.problem_final  SRC JOIN cox_mdwdb.f_problem TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN cox_mdwdb.d_internal_contact LKP ON ( concat( 'INTERNAL_CONTACT~',upper( SRC.u_requester)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.u_requester IS NULL THEN 0 else -1 end)<> (TRGT.requester_c_key) 
