SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_application_c.application_change_control_c_key' ELSE 'SUCCESS' END as Message FROM jhi_mdsdb.cmdb_ci_spkg_final  SRC JOIN jhi_mdwdb.d_application_c TRGT ON (SRC.sys_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN jhi_mdwdb.d_internal_organization LKP ON ( concat( 'GROUP~' ,upper( SRC.change_control)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.change_control IS NULL THEN 0 else -1 end)<> (TRGT.application_change_control_c_key) 
