SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_application_c.package_name' ELSE 'SUCCESS' END as Message FROM jhi_mdsdb.cmdb_ci_spkg_final  SRC JOIN jhi_mdwdb.d_application_c TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE COALESCE(SRC.package_name,'UNSPECIFIED') <> (TRGT.package_name_c) 
