SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_application_c.u_description' ELSE 'SUCCESS' END as Message FROM jhi_mdsdb.cmdb_ci_spkg_final  SRC JOIN jhi_mdwdb.d_application_c TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE COALESCE(SRC.u_description,'UNSPECIFIED') <> (TRGT.u_description_c) 
