SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_alert_incident_c.u_escalation_bridge_c' ELSE 'SUCCESS' END as Message FROM png_mdsdb.incident_alert_final  SRC JOIN png_ccs_mdwdb.d_alert_incident_c TRGT ON (SRC.sys_id = TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE SRC.u_escalation_bridge <> (TRGT.u_escalation_bridge_c) 
