SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_ama_inventory_c.vendor_qualification_c_key' ELSE 'SUCCESS' END as Message FROM png_mdsdb.pg_ot_ama_processing_activity_inventory_c_final  SRC JOIN png_jul_mdwdb.d_ama_inventory_c TRGT ON (SRC.inventory_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN png_jul_mdwdb.d_lov LKP ON ( concat( 'VENDOR_QUALIFICATION_C~AMA_PROCESSING_ACTIVITIES~' ,upper( SRC.vendor_qualification)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.vendor_qualification IS NULL THEN 0 else -1 end)<> (TRGT.vendor_qualification_c_key) 
