SELECT CASE WHEN Count(1) > 0  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN Count(1) > 0  THEN 'MDS to DWH data validation failed for d_ama_inventory_c.ama_inventory_ext_c_key' ELSE 'SUCCESS' END as Message FROM png_mdsdb.pg_ot_ama_processing_activity_inventory_c_final  SRC JOIN png_mdwdb.d_ama_inventory_c TRGT ON (SRC.inventory_id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN png_mdwdb.d_ama_inventory_ext_c LKP ON ( concat( '' ,upper( SRC.inventory_id)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.inventory_id IS NULL THEN 0 else -1 end)<> (TRGT.ama_inventory_ext_c_key) 
