SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_brazil_inventory_c.Vendor_Qualification_c_key' ELSE 'SUCCESS' END as Message FROM png_mdsdb.pg_ot_brazil_processing_activity_inventory_c_final  SRC JOIN png_mdwdb.d_brazil_inventory_c TRGT ON (SRC.Inventory_Id = TRGT.row_id AND SRC.sourceinstance = TRGT.source_id )  LEFT JOIN png_mdwdb.d_lov LKP ON ( concat( 'VENDOR_QUALIFICATION_C~BRAZIL_PROCESSING_ACTIVITIES~' ,upper( SRC.Vendor_Qualification)) = LKP.row_id AND SRC.sourceinstance = LKP.source_id ) WHERE COALESCE(LKP.row_key,CASE WHEN SRC.Vendor_Qualification IS NULL THEN 0 else -1 end)<> (TRGT.Vendor_Qualification_c_key) 
