SELECT CASE WHEN count(1)  THEN 'FAILURE' ELSE 'SUCCESS' END as Result, CASE WHEN count(1)  THEN 'MDS to DWH data validation failed for d_it_health_tierzero_continuity_c.n_of_month_planning_c' ELSE 'SUCCESS' END as Message FROM png_mdsdb.us_tier_0_apps_final  SRC JOIN png_mdwdb.d_it_health_tierzero_continuity_c TRGT ON (CONCAT(SRC.HEXID,'~',SRC.Measures_As_Of)= TRGT.row_id  AND SRC.sourceinstance = TRGT.source_id )  WHERE CAST( SRC.N_of_month_planning   as DECIMAL(10,2)) <> (TRGT.n_of_month_planning_c) 
